css:
.card-grid {
  padding: var(--spacing-xl, 3rem) var(--spacing-md, 1rem);
}

.grid-container {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: var(--spacing-lg, 2rem);
  max-width: 1200px;
  margin: 0 auto;
}

.card {
  background: var(--color-background, white);
  border: 1px solid var(--color-border, #e2e8f0);
  border-radius: var(--radius-lg, 1rem);
  overflow: hidden;
  box-shadow: var(--elevation-sm, 0 1px 2px rgba(0,0,0,0.06));
  transition: all 0.2s ease;
  display: flex;
  flex-direction: column;
}

.card:hover {
  box-shadow: var(--elevation-md, 0 4px 8px rgba(0,0,0,0.08));
  transform: translateY(-2px);
}

.card-header {
  position: relative;
  overflow: hidden;
}

.card-image {
  width: 100%;
  height: 200px;
  object-fit: cover;
  display: block;
}

.card-icon {
  width: 100%;
  height: 120px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, var(--color-primary, #3b82f6), var(--color-secondary, #1d4ed8));
  color: var(--color-background, white);
  font-size: 2rem;
}

.card-content {
  padding: var(--spacing-lg, 2rem);
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: var(--spacing-md, 1rem);
}

.card-title {
  font-size: 1.25rem;
  font-weight: 600;
  margin: 0;
  color: var(--color-text, #1f2937);
  line-height: 1.3;
}

.card-description {
  color: var(--color-text-muted, #6b7280);
  line-height: 1.6;
  margin: 0;
  flex: 1;
}

.card-link {
  color: var(--color-primary, #3b82f6);
  text-decoration: none;
  font-weight: 500;
  display: inline-flex;
  align-items: center;
  gap: var(--spacing-xs, 0.25rem);
  margin-top: auto;
  transition: color 0.2s ease;
}

.card-link:hover {
  color: var(--color-primary-600, #2563eb);
}

.card-link::after {
  content: 'â†’';
  transition: transform 0.2s ease;
}

.card-link:hover::after {
  transform: translateX(4px);
}

@media (max-width: 768px) {
  .grid-container {
    grid-template-columns: 1fr;
    gap: var(--spacing-md, 1rem);
  }

  .card-grid {
    padding: var(--spacing-lg, 2rem) var(--spacing-sm, 0.5rem);
  }

  .card-content {
    padding: var(--spacing-md, 1rem);
  }
}

@media (max-width: 480px) {
  .card-image,
  .card-icon {
    height: 150px;
  }
}
