css:
.dashboard-layout {
  display: grid;
  grid-template-rows: auto 1fr;
  min-height: 100vh;
  background: var(--color-background, #f8fafc);
}

.dashboard-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: var(--spacing-md, 1rem) var(--spacing-lg, 2rem);
  background: var(--color-surface, white);
  border-bottom: 1px solid var(--color-border, #e2e8f0);
  box-shadow: var(--elevation-sm, 0 1px 2px rgba(0,0,0,0.06));
}

.header-left {
  display: flex;
  align-items: center;
  gap: var(--spacing-lg, 2rem);
}

.app-title {
  font-size: 1.5rem;
  font-weight: 700;
  margin: 0;
  color: var(--color-text, #1f2937);
}

.header-right {
  display: flex;
  align-items: center;
  gap: var(--spacing-lg, 2rem);
}

.header-nav {
  display: flex;
  gap: var(--spacing-md, 1rem);
}

.header-link {
  color: var(--color-text-muted, #6b7280);
  text-decoration: none;
  font-weight: 500;
  transition: color 0.2s ease;
}

.header-link:hover {
  color: var(--color-primary, #3b82f6);
}

.user-menu {
  display: flex;
  align-items: center;
  gap: var(--spacing-sm, 0.5rem);
  padding: var(--spacing-xs, 0.25rem) var(--spacing-sm, 0.5rem);
  border-radius: var(--radius-md, 0.5rem);
  transition: background-color 0.2s ease;
  cursor: pointer;
}

.user-menu:hover {
  background: var(--color-surface, #f8fafc);
}

.user-avatar {
  width: 32px;
  height: 32px;
  border-radius: 50%;
  object-fit: cover;
}

.user-name {
  font-weight: 500;
  color: var(--color-text, #1f2937);
}

.dashboard-body {
  display: grid;
  grid-template-columns: 250px 1fr;
  gap: 0;
  overflow: hidden;
}

.dashboard-sidebar {
  background: var(--color-surface, white);
  border-right: 1px solid var(--color-border, #e2e8f0);
  overflow-y: auto;
}

.sidebar-nav {
  padding: var(--spacing-lg, 2rem) 0;
  display: flex;
  flex-direction: column;
  gap: var(--spacing-xs, 0.25rem);
}

.sidebar-item {
  display: flex;
  align-items: center;
  gap: var(--spacing-sm, 0.5rem);
  padding: var(--spacing-sm, 0.5rem) var(--spacing-lg, 2rem);
  color: var(--color-text-muted, #6b7280);
  text-decoration: none;
  font-weight: 500;
  transition: all 0.2s ease;
  position: relative;
}

.sidebar-item:hover,
.sidebar-item.active {
  background: var(--color-primary-50, #eff6ff);
  color: var(--color-primary, #3b82f6);
}

.sidebar-item.active::before {
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  width: 3px;
  background: var(--color-primary, #3b82f6);
}

.sidebar-icon {
  font-size: 1.125rem;
  width: 20px;
  display: flex;
  justify-content: center;
}

.dashboard-main {
  padding: var(--spacing-lg, 2rem);
  overflow-y: auto;
}

.dashboard-grid {
  display: grid;
  grid-template-columns: repeat(12, 1fr);
  gap: var(--spacing-lg, 2rem);
  grid-auto-rows: minmax(200px, auto);
}

.dashboard-widget {
  background: var(--color-surface, white);
  border: 1px solid var(--color-border, #e2e8f0);
  border-radius: var(--radius-lg, 1rem);
  box-shadow: var(--elevation-sm, 0 1px 2px rgba(0,0,0,0.06));
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

.dashboard-widget.small {
  grid-column: span 3;
}

.dashboard-widget.medium {
  grid-column: span 6;
}

.dashboard-widget.large {
  grid-column: span 9;
}

.dashboard-widget.full {
  grid-column: span 12;
}

.widget-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: var(--spacing-lg, 2rem) var(--spacing-lg, 2rem) var(--spacing-md, 1rem);
  border-bottom: 1px solid var(--color-border, #e2e8f0);
}

.widget-title {
  font-size: 1.125rem;
  font-weight: 600;
  margin: 0;
  color: var(--color-text, #1f2937);
}

.widget-actions {
  display: flex;
  gap: var(--spacing-xs, 0.25rem);
}

.widget-action {
  background: none;
  border: none;
  color: var(--color-text-muted, #6b7280);
  cursor: pointer;
  padding: var(--spacing-xs, 0.25rem);
  border-radius: var(--radius-sm, 0.25rem);
  transition: all 0.2s ease;
}

.widget-action:hover {
  background: var(--color-surface, #f8fafc);
  color: var(--color-text, #1f2937);
}

.widget-content {
  flex: 1;
  padding: var(--spacing-lg, 2rem);
  display: flex;
  flex-direction: column;
}

@media (max-width: 1024px) {
  .dashboard-body {
    grid-template-columns: 200px 1fr;
  }

  .dashboard-grid {
    grid-template-columns: repeat(8, 1fr);
  }

  .dashboard-widget.small {
    grid-column: span 4;
  }

  .dashboard-widget.medium {
    grid-column: span 8;
  }

  .dashboard-widget.large {
    grid-column: span 8;
  }
}

@media (max-width: 768px) {
  .dashboard-header {
    padding: var(--spacing-sm, 0.5rem) var(--spacing-md, 1rem);
  }

  .header-nav {
    display: none;
  }

  .dashboard-body {
    grid-template-columns: 1fr;
  }

  .dashboard-sidebar {
    display: none;
  }

  .dashboard-main {
    padding: var(--spacing-md, 1rem);
  }

  .dashboard-grid {
    grid-template-columns: 1fr;
    gap: var(--spacing-md, 1rem);
  }

  .dashboard-widget {
    grid-column: span 1;
  }
}
