css:
.hero {
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 70vh;
  padding: var(--spacing-xl, 3rem) var(--spacing-md, 1rem);
  background: linear-gradient(135deg, var(--color-primary, #3b82f6), var(--color-secondary, #1d4ed8));
  color: var(--color-background, white);
  text-align: center;
}

.hero-content {
  max-width: 800px;
  display: flex;
  flex-direction: column;
  gap: var(--spacing-lg, 2rem);
}

.hero-title {
  font-size: clamp(2rem, 5vw, 4rem);
  font-weight: 700;
  line-height: 1.2;
  margin: 0;
}

.hero-subtitle {
  font-size: clamp(1rem, 2.5vw, 1.5rem);
  line-height: 1.6;
  opacity: 0.9;
  margin: 0;
}

.hero-actions {
  display: flex;
  gap: var(--spacing-md, 1rem);
  justify-content: center;
  flex-wrap: wrap;
}

@media (max-width: 768px) {
  .hero {
    min-height: 50vh;
    padding: var(--spacing-lg, 2rem) var(--spacing-sm, 0.5rem);
  }

  .hero-actions {
    flex-direction: column;
    align-items: center;
  }
}
